sys_script_include.script:
var NeeditGetEmailOfCurrentUser = Class.create();
NeeditGetEmailOfCurrentUser.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    // Define the getEmail function.  
    // Create a GlideRecord for the User table.
    // Use the sysparm_userID passed from the client side to retrieve a record from the User table.
    // Return the email address for the requested record
    getEmailGetUser: function() {
        var loggedInUser = gs.getUserID();
        var currentUser = new GlideRecord('sys_user');
        currentUser.addQuery('sys_id', this.getParameter('sysparm_user'));
        currentUser.query();
        while (currentUser.next()) {
            var currentEmail = currentUser.email;
        }
        return currentEmail;
    },
    type: 'NeeditGetEmailOfCurrentUser'
});
